/* Breakpoint detection system */
(function() {
    'use strict';

    window.breakpoints = {
        xlarge: ['1281px', '1680px'],
        large: ['981px', '1280px'],
        medium: ['737px', '980px'],
        small: ['481px', '736px'],
        xsmall: [null, '480px'],
        
        active: function(breakpoint) {
            var width = window.innerWidth;
            var bp = this[breakpoint];
            
            if (!bp) return false;
            
            var min = bp[0] ? parseInt(bp[0]) : 0;
            var max = bp[1] ? parseInt(bp[1]) : Infinity;
            
            return width >= min && width <= max;
        },
        
        on: function(query, callback) {
            var mediaQuery = window.matchMedia(query);
            mediaQuery.addListener(callback);
            callback(mediaQuery);
        }
    };

})();