/* jQuery Scrolly plugin placeholder */
(function($) {
    $.fn.scrolly = function(options) {
        return this.on('click', function(e) {
            var $this = $(this);
            var href = $this.attr('href');
            
            if (href && href.indexOf('#') === 0) {
                var target = $(href);
                if (target.length) {
                    e.preventDefault();
                    $('html, body').animate({
                        scrollTop: target.offset().top
                    }, options && options.speed || 500);
                }
            }
        });
    };
})(jQuery);